    {{-- pie chart --}}
    <script type="text/javascript">
        var analytics = [
            ['Month', 'User'],
            ['Jan', 100],
            ['Feb', 200],
            ['Mar', 300],
            ['Apr', 400],
            ['May', 500],
            ['Jun', 600],
            ['Jul', 700],
            ['Aug', 800],
            ['Sep', 900],
